package Looping;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 5;
		
		System.out.println("Do while loop");
		
		do{
			System.out.println("Do While loop's This Should not be printed");
			
		}while(count!=5);
		
		System.out.println("while loop");
		
		while(count!=5){
			System.out.println("While loop's This Should not be printed");
			
		}
		
	}

}
