package controlFlow;

public class TernaryOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =1;
		int b = 2;
		int result;
		
	//  variable = conditional_expression ? if true : if false 
		result = a < b ? a : b;
		
		System.out.println("Result = "+ result);
	}

}
